import Link from 'next/link';
import Env from '../config/config';
import axios from 'axios';
import { MD5 } from 'crypto-js';

const linkStyle = {
  marginRight: '20px',
};

const Characters = ({characters}) => {

  const getCharacters = characters.map((character) =>
    <li> {character.id} - {character.name} </li>
  )

  return (

    <section>
      <Link href='/'>
        <a style={linkStyle}>Home</a>
      </Link>
      <Link href='/comics'>
        <a style={linkStyle}>Comics</a>
      </Link>

      <h1>Characters</h1>
      <ul> { getCharacters } </ul>
    </section>

  )
};

export async function getStaticProps() {
  const publicKey = Env.publicKey, privateKey = Env.privateKey;
  const ts = new Date().getTime();
  const hash = MD5(ts + privateKey + publicKey);
  const url = `https://gateway.marvel.com:443/v1/public/characters?limit=20&ts=${ts}&apikey=${publicKey}&hash=${hash}`;

  const req = await axios.get(url);


  return {
    props: {
      characters: req.data.data.results
    }
  }

}

export default Characters;
